package com.esgi.promocare_android.utils

import android.widget.ImageView
import com.bumptech.glide.Glide
import com.bumptech.glide.load.resource.bitmap.CenterCrop
import com.bumptech.glide.load.resource.bitmap.RoundedCorners
import com.bumptech.glide.request.RequestOptions
import com.esgi.promocare_android.R

val menage : String = "data:image/jpeg;base64,"
val cuisine : String = "data:image/jpeg;base64,"
val conduite : String = "data:image/jpeg;base64,"
val soin: String = "data:image/jpeg;base64,"




fun loadImage(imageView: ImageView,annonceType:String?) {
    if(annonceType == null){
        return
    }
    var resString: String = menage
    if(annonceType == "Menage"){
        resString = menage
    }
    else if(annonceType == "Cuisine"){
        resString = cuisine
    }
    else if(annonceType == "Chauffeur"){
        resString = conduite
    }
    else if(annonceType == "Soin"){
        resString = soin
    }

    Glide.with(imageView.context)
        .load(resString)
        .apply(RequestOptions().transform(CenterCrop(), RoundedCorners(50)))
        .placeholder(R.drawable.loader_glide) // Optional placeholder
        .error(R.drawable.error_black) // Optional error image
        .into(imageView)
}